mapper.select:
select count(*) 
from dealer_employee_dealer_department 
where dealer_inner_sn = #{id} and department_inner_sn = #{department_inner_sn}

select count(*) 
from dealer_warehouse_model_dealer_department 
where dealer_inner_sn = #{id} and department_inner_sn = #{department_inner_sn}

 		select sum(c) 
 		from(
			select count(*) as c from dealer_ppl where dealer_inner_sn = #{id} and department_inner_sn = #{department_inner_sn}
			union all
			select count(*) as c from dealer_po_dealer_so where dealer_a_inner_sn = #{id} and purchase_department_inner_sn = #{department_inner_sn}
			union all
			select count(*) as c from dealer_po_vendor_so where dealer_inner_sn = #{id} and purchase_department_inner_sn = #{department_inner_sn}
			union all
			select count(*) as c from dealer_dealer_grn where dealer_inner_sn = #{id} and department_inner_sn = #{department_inner_sn}
			union all
			select count(*) as c from dealer_hospital_grn where dealer_inner_sn = #{id} and department_inner_sn = #{department_inner_sn}
			union all
			select count(*) as c from dealer_vendor_grn where dealer_inner_sn = #{id} and department_inner_sn = #{department_inner_sn}
			union all
			select count(*) as c from dealer_dealer_odo where dealer_inner_sn = #{id} and department_inner_sn = #{department_inner_sn}
			union all
			select count(*) as c from dealer_hospital_odo where dealer_inner_sn = #{id} and department_inner_sn = #{department_inner_sn}
			union all
			select count(*) as c from dealer_vendor_odo where dealer_inner_sn = #{id} and department_inner_sn = #{department_inner_sn}
			union all
			select count(*) as c from dealer_dealer_pp where dealer_inner_sn = #{id} and department_inner_sn = #{department_inner_sn}
			union all
			select count(*) as c from dealer_vendor_pp where dealer_inner_sn = #{id} and department_inner_sn = #{department_inner_sn}
			union all
			select count(*) as c from dealer_dealer_rn where dealer_inner_sn = #{id} and department_inner_sn = #{department_inner_sn}
			union all
			select count(*) as c from dealer_vendor_rn where dealer_inner_sn = #{id} and department_inner_sn = #{department_inner_sn}
			union all
			select count(*) as c from dealer_dealer_sl where dealer_inner_sn = #{id} and department_inner_sn = #{department_inner_sn}
			union all
			select count(*) as c from dealer_hospital_sl where dealer_inner_sn = #{id} and department_inner_sn = #{department_inner_sn}
		) a


where 1=1 
	and e.dealer_inner_sn = #{id} and e.employee_inner_sn = #{employee_inner_sn}
group by e.dealer_inner_sn, e.employee_inner_sn


where 1=1
and e.dealer_inner_sn = #{id} and e.user_inner_sn = #{user_inner_sn}
group by e.dealer_inner_sn, e.employee_inner_sn


where e.is_delete = 0 
	and e.dealer_inner_sn = #{id} and e.parent_employee_inner_sn = #{parent_employee_inner_sn}
group by e.dealer_inner_sn, e.employee_inner_sn

