mapper.select:
		SELECT product_vendor_inner_sn, product_vendor_name, product_inner_sn, 
			product_name, specification_inner_sn, specification, unit_inner_sn, unit, unit_price/10000 as price, 1 as product_type
		FROM vendor_dealer_odo_product_detail d
		JOIN(
	        SELECT vendor_inner_sn, o.odo_inner_sn
	        FROM vendor_dealer_odo_product_detail
	        JOIN vendor_dealer_odo o using(vendor_inner_sn,odo_inner_sn)
	        WHERE product_inner_sn = #{product_inner_sn}
	        AND specification_inner_sn = #{specification_inner_sn}
	        AND product_vendor_inner_sn = #{product_vendor_inner_sn}
	        AND vendor_inner_sn = #{org_id}  AND organization_inner_sn = #{organization_inner_sn}
	        order by odo_inner_sn desc  limit 1
		) o using(vendor_inner_sn,odo_inner_sn)
		WHERE product_inner_sn = #{product_inner_sn}
		AND specification_inner_sn = #{specification_inner_sn}
        AND product_vendor_inner_sn = #{product_vendor_inner_sn}

select distinct w.vendor_inner_sn as org_id, h.name as org_name, w.odo_inner_sn, w.organization_inner_sn, w.organization_name, w.auditor_inner_sn, w.auditor_name,w.warehouse_inner_sn, w.warehouse_name, w.handler_inner_sn, IFNULL(w.printed,0) as printed, w.handler_name, w.making_people_inner_sn, w.making_people_name, w.warehouse_operator_inner_sn, w.warehouse_operator_name, w.type_inner_sn, w.type_name, w.department_inner_sn, w.department_name, w.status, w.out_time, w.odo_sn, w.description, 6 as billsType from vendor_dealer_odo w left join vendor h on h.vendor_inner_sn = w.vendor_inner_sn INNER JOIN ( SELECT vendor_inner_sn,odo_inner_sn FROM vendor_dealer_odo_product_detail where 1=1 and product_name like concat('%', #{product_name}, '%') )d ON d.`vendor_inner_sn`=w.`vendor_inner_sn` AND d.`odo_inner_sn`=w.`odo_inner_sn` where 1=1 and w.vendor_inner_sn = #{org_id} and w.odo_sn like concat('%', #{odo_sn}, '%') and w.warehouse_inner_sn = #{warehouse_model_inner_sn} and w.handler_name like concat('%', #{handler_name}, '%') AND date_format(w.out_time,'%y%m%d') >= date_format(#{begin_date},'%y%m%d') AND date_format(w.out_time,'%y%m%d') <= date_format(#{end_date},'%y%m%d') AND w.status = #{status} AND w.type_inner_sn = #{type_inner_sn}
AND (w.odo_sn like concat('%', #{key}, '%') or w.organization_name like concat('%', #{key}, '%') or w.making_people_name like concat('%', #{key}, '%') or w.warehouse_operator_name like concat('%', #{key}, '%') )
AND w.`warehouse_inner_sn` IN
#{warehouseId}
union all select distinct w.vendor_inner_sn as org_id, h.name as org_name, w.odo_inner_sn, w.organization_inner_sn, w.organization_name, w.auditor_inner_sn, w.auditor_name,w.warehouse_inner_sn, w.warehouse_name, w.handler_inner_sn, IFNULL(w.printed,0) as printed, w.handler_name, w.making_people_inner_sn, w.making_people_name, w.warehouse_operator_inner_sn, w.warehouse_operator_name, w.type_inner_sn, w.type_name, w.department_inner_sn, w.department_name, w.status, w.out_time, w.odo_sn, w.description, 7 as billsType from vendor_hospital_odo w left join vendor h on h.vendor_inner_sn = w.vendor_inner_sn INNER JOIN ( SELECT vendor_inner_sn,odo_inner_sn FROM vendor_hospital_odo_product_detail where 1=1 and product_name like concat('%', #{product_name}, '%') )d ON d.`vendor_inner_sn`=w.`vendor_inner_sn` AND d.`odo_inner_sn`=w.`odo_inner_sn` where 1=1 and w.vendor_inner_sn = #{org_id} and w.odo_sn like concat('%', #{odo_sn}, '%') and w.warehouse_inner_sn = #{warehouse_model_inner_sn} and w.handler_name like concat('%', #{handler_name}, '%') AND date_format(w.out_time,'%y%m%d') >= date_format(#{begin_date},'%y%m%d') AND date_format(w.out_time,'%y%m%d') <= date_format(#{end_date},'%y%m%d') AND w.status = #{status} AND w.type_inner_sn = #{type_inner_sn}
AND (w.odo_sn like concat('%', #{key}, '%') or w.organization_name like concat('%', #{key}, '%') or w.making_people_name like concat('%', #{key}, '%') or w.warehouse_operator_name like concat('%', #{key}, '%') )
AND w.`warehouse_inner_sn` IN
#{warehouseId}

mapper.update:
update vendor_dealer_odo
organization_inner_sn = #{organization_inner_sn}, organization_name = #{organization_name}, warehouse_type_inner_sn = #{warehouse_type_inner_sn}, warehouse_inner_sn = #{warehouse_inner_sn}, warehouse_name = #{warehouse_name}, handler_inner_sn = #{handler_inner_sn}, handler_name = #{handler_name}, making_people_inner_sn = #{making_people_inner_sn}, making_people_name = #{making_people_name}, auditor_inner_sn = #{auditor_inner_sn}, auditor_name = #{auditor_name}, warehouse_operator_inner_sn = #{warehouse_operator_inner_sn}, warehouse_operator_inner_sn = null, warehouse_operator_name = #{warehouse_operator_name}, type_inner_sn = #{type_inner_sn}, type_name = #{type_name}, department_inner_sn = #{department_inner_sn}, department_inner_sn = null, department_name = #{department_name}, status = #{status}, odo_sn = #{odo_sn}, out_time = #{out_time}, description = #{description}, use_department = #{use_department}, printed = #{printed},
where 1=1 and vendor_inner_sn = #{org_id} and odo_inner_sn = #{odo_inner_sn}

UPDATE vendor_dealer_odo_product_detail
unit_inner_sn = #{unit_inner_sn}, mdrf_inner_sn = #{mdrf_inner_sn}, unit_price = #{unit_price}, quantity = #{quantity}, produce_date = #{produce_date}, produce_date = null, overdue_date = #{overdue_date}, type = #{type}, sort_number = #{sort_number}, batch_number = #{batch_number}, unit = #{unit}, mdrf_sn = #{mdrf_sn}, health_care_sn = #{health_care_sn},
WHERE vendor_inner_sn = #{org_id} AND odo_inner_sn = #{odo_inner_sn} AND detail_inner_sn = #{detail_inner_sn}

mapper.delete:
delete from vendor_dealer_odo_product_detail 
where vendor_inner_sn = #{org_id} and odo_inner_sn = #{odo_inner_sn}
and detail_inner_sn = #{detail_inner_sn}

mapper.select:
        select batch_number, produce_date, overdue_date, quantity,CAST(sum((average_price/10000)*(quantity/1000))/(sum(quantity)/1000) AS DECIMAL(16,4)) AS average_price
		from vendor_product_min_unit_inventory
        where 1=1 and quantity != 0
	        and vendor_inner_sn = #{org_id}
	        AND (date_format(now(),'%y%m%d') <= date_format(overdue_date,'%y%m%d') or overdue_date is null)
            and warehouse_inner_sn = #{warehouse_inner_sn}
			and product_vendor_inner_sn = #{product_vendor_inner_sn}
			and product_inner_sn = #{product_inner_sn}
			and specification_inner_sn = #{specification_inner_sn}
			and batch_number = #{batch_number}
		group by product_inner_sn, product_name, specification_inner_sn, warehouse_inner_sn, batch_number, produce_date, overdue_date
		order by produce_date

		SELECT voucher_inner_sn_c, voucher_detail_inner_sn_c, measure, measure *srcQuantity as srcQuantity, totalQuantity, measure *srcQuantity-totalQuantity as fixQuantity, 
			'VD' as billsType, product_name, specification
		FROM(
	        SELECT vendor_inner_sn AS org_id, sl_inner_sn AS voucher_inner_sn_c, detail_inner_sn AS voucher_detail_inner_sn_c, product_name, specification,
                c_calculate_product_min_unit(product_vendor_inner_sn, product_inner_sn, specification_inner_sn, unit_inner_sn) as measure, sum(quantity) AS srcQuantity
	        FROM vendor_dealer_sl_product_detail
	        WHERE vendor_inner_sn = #{org_id} AND sl_inner_sn = #{voucherId}
	        and detail_inner_sn = #{voucherDetailId}
	        GROUP BY vendor_inner_sn, sl_inner_sn, detail_inner_sn
		) a
		INNER JOIN(
			SELECT IFNULL(SUM(c_calculate_product_min_unit(product_vendor_inner_sn, product_inner_sn, specification_inner_sn, unit_inner_sn) * quantity),0) AS totalQuantity,
	        	vendor_inner_sn as org_id, voucher_inner_sn_c, voucher_detail_inner_sn_c
			FROM vendor_dealer_odo_product_detail 
			WHERE voucher_type_c = #{voucherType} AND vendor_inner_sn = #{org_id} AND voucher_inner_sn_c = #{voucherId}
			and voucher_detail_inner_sn_c = #{voucherDetailId}
			GROUP BY vendor_inner_sn, voucher_inner_sn_c, voucher_detail_inner_sn_c
		) b USING(org_id, voucher_inner_sn_c, voucher_detail_inner_sn_c)

SELECT id, vendor_inner_sn as org_id, warehouse_inner_sn, out_time, odo_sn as sn, warehouse_name, product_vendor_inner_sn, product_inner_sn, specification_inner_sn, unit_inner_sn, mdrf_inner_sn, unit_price/10000 as unit_price, quantity/1000 as quantity, produce_date, overdue_date, batch_number, product_vendor_name, product_name, specification, unit, mdrf_sn, health_care_sn, rate, product_type, buying_price/10000 as buying_price, 7 as voucher_type_c, g.odo_inner_sn as voucher_inner_sn_c, d.detail_inner_sn as voucher_detail_inner_sn_c FROM vendor_dealer_odo g JOIN ( SELECT concat(odo_inner_sn,'-',detail_inner_sn) as id, vendor_inner_sn, odo_inner_sn, product_vendor_inner_sn, product_inner_sn, specification_inner_sn, unit_inner_sn, mdrf_inner_sn, unit_price, quantity, produce_date, overdue_date, batch_number, product_vendor_name, product_name, specification, unit, mdrf_sn, buying_price as buying_price, health_care_sn, rate, 1 as product_type,detail_inner_sn FROM vendor_dealer_odo_product_detail WHERE 1 = 1 and product_vendor_name = #{product_vendor_name} and product_name like concat('%', #{product_name}, '%') and specification like concat('%', #{specification}, '%') ) d USING(vendor_inner_sn, odo_inner_sn) WHERE g.status in (2,3) and type_inner_sn in (0,3,5) and vendor_inner_sn = #{org_id} and organization_inner_sn = #{organization_inner_sn}
and g.warehouse_inner_sn in
#{warehouseId}
and g.odo_sn like concat('%', #{sn}, '%') AND date_format(g.out_time,'%y%m%d') >= date_format(#{begin_date},'%y%m%d') AND date_format(g.out_time,'%y%m%d') <= date_format(#{end_date},'%y%m%d')

mapper.sql:
SELECT id, vendor_inner_sn as org_id,'VD' as billsType, out_time, odo_sn as sn, product_vendor_inner_sn, product_inner_sn, odo_inner_sn AS voucher_inner_sn_c, detail_inner_sn AS voucher_detail_inner_sn_c, 7 as voucher_type_c, specification_inner_sn, unit_inner_sn, mdrf_inner_sn, unit_price, quantity-invoiced_quantity as quantity, produce_date, overdue_date, batch_number, product_vendor_name, product_name, specification, unit, mdrf_sn, health_care_sn, rate, product_type, buying_price/10000 as buying_price,g.organization_inner_sn,g.organization_name FROM vendor_dealer_odo g JOIN ( SELECT concat(odo_inner_sn,'-',detail_inner_sn) as id, vendor_inner_sn, odo_inner_sn, detail_inner_sn, product_vendor_inner_sn, product_inner_sn, specification_inner_sn, unit_inner_sn, mdrf_inner_sn, unit_price/10000 as unit_price, quantity/1000 as quantity, produce_date, overdue_date, batch_number, product_vendor_name, product_name, specification, unit, mdrf_sn, buying_price as buying_price, IFNULL(invoiced_quantity,0) as invoiced_quantity, health_care_sn, rate, 1 as product_type FROM vendor_dealer_odo_product_detail d LEFT JOIN ( SELECT sum(invoiced_quantity) as invoiced_quantity, voucher_inner_sn_c, voucher_detail_inner_sn_c FROM system_invoice_detail d INNER JOIN system_invoice m ON m.id = d.system_invoice_id WHERE m.is_cancel = 0 and system_type = 3 and voucher_type_c = 7 and m.org_id = #{org_id} and organization_inner_sn = #{organization_inner_sn} GROUP BY voucher_inner_sn_c, voucher_detail_inner_sn_c ) a on d.odo_inner_sn = a.voucher_inner_sn_c and d.detail_inner_sn = a.voucher_detail_inner_sn_c WHERE 1 = 1 and product_vendor_name = #{product_vendor_name} and product_name like concat('%', #{product_name}, '%') and specification like concat('%', #{specification}, '%') ) d USING(vendor_inner_sn, odo_inner_sn) WHERE g.status in (2,3) and type_inner_sn in (0,5) and quantity > invoiced_quantity and vendor_inner_sn = #{org_id} and organization_inner_sn = #{organization_inner_sn}
and g.warehouse_inner_sn in
#{warehouseId}
and g.odo_sn like concat('%', #{sn}, '%') AND date_format(g.out_time,'%y%m%d') >= date_format(#{begin_date},'%y%m%d') AND date_format(g.out_time,'%y%m%d') <= date_format(#{end_date},'%y%m%d') UNION ALL SELECT id, vendor_inner_sn as org_id,'VD' as billsType, out_time, odo_sn as sn, product_vendor_inner_sn, product_inner_sn, odo_inner_sn AS voucher_inner_sn_c, detail_inner_sn AS voucher_detail_inner_sn_c, 7 as voucher_type_c, specification_inner_sn, unit_inner_sn, mdrf_inner_sn, unit_price, quantity-invoiced_quantity as quantity, produce_date, overdue_date, batch_number, product_vendor_name, product_name, specification, unit, mdrf_sn, health_care_sn, rate, product_type, buying_price/10000 as buying_price,g.organization_inner_sn,g.organization_name FROM vendor_dealer_odo g JOIN ( SELECT concat(odo_inner_sn,'-',detail_inner_sn) as id, vendor_inner_sn, odo_inner_sn, detail_inner_sn, product_vendor_inner_sn, product_inner_sn, specification_inner_sn, unit_inner_sn, mdrf_inner_sn, unit_price/10000 as unit_price, quantity/1000 as quantity, produce_date, overdue_date, batch_number, product_vendor_name, product_name, specification, unit, mdrf_sn, buying_price as buying_price, IFNULL(invoiced_quantity,0) as invoiced_quantity, health_care_sn, rate, 1 as product_type FROM vendor_dealer_odo_product_detail d LEFT JOIN ( SELECT sum(invoiced_quantity) as invoiced_quantity, voucher_inner_sn_c, voucher_detail_inner_sn_c FROM system_invoice_detail d INNER JOIN system_invoice m ON m.id = d.system_invoice_id WHERE m.is_cancel = 0 and system_type = 3 and voucher_type_c = 7 and m.org_id = #{org_id} and organization_inner_sn = #{organization_inner_sn} GROUP BY voucher_inner_sn_c, voucher_detail_inner_sn_c ) a on d.odo_inner_sn = a.voucher_inner_sn_c and d.detail_inner_sn = a.voucher_detail_inner_sn_c WHERE 1 = 1 and product_vendor_name = #{product_vendor_name} and product_name like concat('%', #{product_name}, '%') and specification like concat('%', #{specification}, '%') ) d USING(vendor_inner_sn, odo_inner_sn) WHERE g.status in (2,3) and type_inner_sn = 1 and quantity < invoiced_quantity and vendor_inner_sn = #{org_id} and organization_inner_sn = #{organization_inner_sn}
and g.warehouse_inner_sn in
#{warehouseId}
and g.odo_sn like concat('%', #{sn}, '%') AND date_format(g.out_time,'%y%m%d') >= date_format(#{begin_date},'%y%m%d') AND date_format(g.out_time,'%y%m%d') <= date_format(#{end_date},'%y%m%d')

mapper.select:
	SELECT health_care_sn
	FROM vendor_dealer_odo g
	JOIN (
        SELECT vendor_inner_sn, odo_inner_sn, health_care_sn
        FROM vendor_dealer_odo_product_detail
        WHERE 1 = 1
			and product_vendor_inner_sn = #{vendor_inner_sn}
        	and product_inner_sn = #{product_inner_sn}
        	and specification_inner_sn = #{specification_inner_sn}
	) d USING(vendor_inner_sn, odo_inner_sn)
	WHERE g.status in (2,3) and vendor_inner_sn = #{org_id} and organization_inner_sn = #{organization_inner_sn}
	ORDER BY out_time desc limit 1
